package syntaxtree;import visitor.InterpreterVisitor;import visitor.Visitor;import visitor.TypeVisitor;public class Assign extends Statement {  public Identifier i;  public Exp e;  public Assign(Identifier ai, Exp ae, Position p) {    i=ai; e=ae; position=p;  }  public void accept(Visitor v) {    v.visit(this);  }  public Type accept(TypeVisitor v) {    return v.visit(this);  }    public Object accept(InterpreterVisitor v) {	  return v.visit(this);  }}