package syntaxtree;import visitor.InterpreterVisitor;import visitor.Visitor;import visitor.TypeVisitor;public class Block extends Statement {  public StatementList sl;  public Block(StatementList asl, Position p) {    sl=asl;position=p;  }  public void accept(Visitor v) {    v.visit(this);  }  public Type accept(TypeVisitor v) {    return v.visit(this);  }    public Object accept(InterpreterVisitor v) {	  return v.visit(this);  }}