package syntaxtree;import visitor.BuildSymbolTableVisitor;import visitor.Visitor;import visitor.TypeVisitor;public class IntegerType extends Type {	public IntegerType() {}	public IntegerType(Position p) {		position=p;	}  public void accept(Visitor v) {    v.visit(this);  }  public Type accept(TypeVisitor v) {    return v.visit(this);  }      public Type accept(BuildSymbolTableVisitor v){	  return new IntegerType();  }    public String toString() {  	  return "int";  }}