package syntaxtree;import visitor.InterpreterVisitor;import visitor.Visitor;import visitor.TypeVisitor;public class MainClass {  public Identifier i1,i2;  public Statement s;  public Position position = null;    public MainClass(Identifier ai1, Identifier ai2, Statement as, Position p) {    i1=ai1; i2=ai2; s=as;    position = p;  }  public void accept(Visitor v) {    v.visit(this);  }    public Object accept(InterpreterVisitor v) {	  return v.visit(this);  }  public Type accept(TypeVisitor v) {    return v.visit(this);  }}